import { useEffect, useState } from "react";

function News() {

  const [Posts, setPosts] = useState([]);

  useEffect(() => {
    let data = localStorage.getItem('post');
    data = JSON.parse(data);
    setPosts(data);
  }, []);

  return(

    <main id="news">
      <h1>News</h1>
      {Posts.map((post, idx) => {
        if(idx >= 5) return; // 5개의 인덱스만 가져다 달라는 의미

        return(
          <article key={idx}>
            <h2>{post.title}</h2>
            <p>{post.content}</p>
          </article>
        );
      })}
    </main>
  );
}

export default News;
