import Layout from "../common/Layout"
import axios from "axios";
import { useEffect, useState } from "react";

export default function Youtube() {


  const [Vids, setVids] = useState([]);

  useEffect(() => {
    const key = 'AIzaSyCtN1lqIIdi7ibHkYVCtVtP9vA4oz8j8n8';
    const playList = 'PLlXUbM-Wv86W_pA2wzZgQ7pF1VeHP6At4';
    const num = 6; // 재생목록의 불러올 비디오 갯수
    const url = `https://www.googleapis.com/youtube/v3/playlistItems?part=snippet&key=${key}&playlistId=${playList}&maxResults=${num}`;

    axios.get(url).then((json) => {
      console.log(json.data);
      setVids(json.data.items);
    })
  });

  return (
    <Layout name={'Youtube'}>

      {Vids.map((data,index) => {
        let title = data.snippet.title;
        let con = data.snippet.description;
        let date = data.snippet.publishedAt;

        if(title.length > 30) {
          title = title.substr(0, 30) + '...';
        }
        if(con.length > 100) {
        con = con.substr(0, 100) + '...';
        }
        date = date.split('T')[0];

        return(
          <article key={index}>
            <h3>{title}</h3>
            <div className="txt">
              <p>{con}</p>
              <span>{date}</span>
            </div>
            <div className="pic">
              <img src={data.snippet.thumbnails.standard.url} alt={title} />
            </div>
          </article>
        );
      })}
    </Layout>
  )
}
